import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    nombre: "",
    descripcion: "",
    precio: "",
    cantidad: "",
    categoria: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [activeView, setActiveView] = useState("agregar");
  const [notification, setNotification] = useState({ show: false, message: "", type: "" });

  useEffect(() => {
    fetchProductos();
  }, []);

  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });
    setTimeout(() => setNotification({ ...notification, show: false }), 3000);
  };

  const fetchProductos = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/productos");
      const data = await response.json();
      setProductos(data);
    } catch (error) {
      console.error("Error fetching productos:", error);
      showNotification("Error al cargar productos", "error");
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = editingId
        ? `http://localhost:5000/api/productos/${editingId}`
        : "http://localhost:5000/api/productos";
      const method = editingId ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        fetchProductos();
        setFormData({
          nombre: "",
          descripcion: "",
          precio: "",
          cantidad: "",
          categoria: "",
        });
        setEditingId(null);
        showNotification(
          editingId ? "Producto actualizado correctamente" : "Producto agregado correctamente",
          "success"
        );
        setActiveView("listar");
      }
    } catch (error) {
      console.error("Error saving producto:", error);
      showNotification("Error al guardar el producto", "error");
    }
  };

  const handleEdit = (producto) => {
    setFormData({
      nombre: producto.nombre,
      descripcion: producto.descripcion,
      precio: producto.precio,
      cantidad: producto.cantidad,
      categoria: producto.categoria,
    });
    setEditingId(producto.id);
    setActiveView("agregar");
  };

  const handleDelete = async (id) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este producto?")) {
      try {
        const response = await fetch(
          `http://localhost:5000/api/productos/${id}`,
          {
            method: "DELETE",
          }
        );

        if (response.ok) {
          fetchProductos();
          showNotification("Producto eliminado correctamente", "success");
        }
      } catch (error) {
        console.error("Error deleting producto:", error);
        showNotification("Error al eliminar el producto", "error");
      }
    }
  };

  return (
    <div className="app">
      <div className="sidebar">
        <h2 className="sidebar-title">Menú</h2>
        <ul className="sidebar-menu">
          <li
            className={activeView === "agregar" ? "active" : ""}
            onClick={() => setActiveView("agregar")}
          >
            Agregar Producto
          </li>
          <li
            className={activeView === "listar" ? "active" : ""}
            onClick={() => setActiveView("listar")}
          >
            Listar Productos
          </li>
        </ul>
      </div>

      <div className="main-content">
        <h1>Inventario de Tienda Online</h1>

        {notification.show && (
          <div className={`notification ${notification.type}`}>
            {notification.message}
          </div>
        )}

        {activeView === "agregar" && (
          <div className="form-container">
            <h2>{editingId ? "Editar Producto" : "Agregar Producto"}</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Nombre:</label>
                <input
                  type="text"
                  name="nombre"
                  value={formData.nombre}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Descripción:</label>
                <textarea
                  name="descripcion"
                  value={formData.descripcion}
                  onChange={handleInputChange}
                />
              </div>

              <div className="form-group">
                <label>Precio:</label>
                <input
                  type="number"
                  name="precio"
                  value={formData.precio}
                  onChange={handleInputChange}
                  step="0.01"
                  required
                />
              </div>

              <div className="form-group">
                <label>Cantidad:</label>
                <input
                  type="number"
                  name="cantidad"
                  value={formData.cantidad}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Categoría:</label>
                <input
                  type="text"
                  name="categoria"
                  value={formData.categoria}
                  onChange={handleInputChange}
                />
              </div>

              <div className="form-actions">
                <button type="submit">
                  {editingId ? "Actualizar" : "Agregar"}
                </button>
                {editingId && (
                  <button
                    type="button"
                    onClick={() => {
                      setFormData({
                        nombre: "",
                        descripcion: "",
                        precio: "",
                        cantidad: "",
                        categoria: "",
                      });
                      setEditingId(null);
                    }}
                  >
                    Cancelar
                  </button>
                )}
              </div>
            </form>
          </div>
        )}

        {activeView === "listar" && (
          <div className="productos-container">
            <h2>Lista de Productos</h2>
            <table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Descripción</th>
                  <th>Precio</th>
                  <th>Cantidad</th>
                  <th>Categoría</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {productos.map((producto) => (
                  <tr key={producto.id}>
                    <td>{producto.nombre}</td>
                    <td>{producto.descripcion}</td>
                    <td>${producto.precio}</td>
                    <td>{producto.cantidad}</td>
                    <td>{producto.categoria}</td>
                    <td className="actions">
                      <button onClick={() => handleEdit(producto)}>Editar</button>
                      <button onClick={() => handleDelete(producto.id)}>
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
